-- Create a function to automatically create a profile when a user signs up
CREATE OR REPLACE FUNCTION public.handle_new_user()
RETURNS TRIGGER AS $$
BEGIN
  INSERT INTO public.profiles (user_id, membership_status)
  VALUES (NEW.id, 'inactive');
  RETURN NEW;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- Create a trigger that calls the function when a new user is created
DROP TRIGGER IF EXISTS on_auth_user_created ON auth.users;
CREATE TRIGGER on_auth_user_created
  AFTER INSERT ON auth.users
  FOR EACH ROW EXECUTE FUNCTION public.handle_new_user();

-- Create workshop waitlist table
create table if not exists public.workshop_waitlist (
  id uuid primary key default gen_random_uuid(),
  email text not null,
  user_id uuid references auth.users(id) on delete cascade,
  created_at timestamptz default timezone('utc'::text, now()) not null,
  unique(email)
);

-- Enable RLS
alter table public.workshop_waitlist enable row level security;

-- Create policies
create policy "Anyone can join waitlist"
  on public.workshop_waitlist
  for insert
  with check (true);

create policy "Users can view their own waitlist entry"
  on public.workshop_waitlist
  for select
  using (auth.uid() = user_id or email = (select email from auth.users where id = auth.uid()));

-- Create index for faster lookups
create index if not exists workshop_waitlist_email_idx on public.workshop_waitlist(email);
create index if not exists workshop_waitlist_user_id_idx on public.workshop_waitlist(user_id);
